import {combineReducers} from 'redux';
import {reducer as formReducer} from 'redux-form';

/**
 * ApiReducer is an autogenerated set of reducers based off ApiSchema
 * The files in the /api directory are required to make the examples work,
 * but not required for general usage of entity editor
 */
import {ApiSchema} from 'api/Api';
import ApiReducer from 'api/ApiReducer';

export default combineReducers({
    form: formReducer,
    ...ApiReducer(ApiSchema)
});
